INSERT INTO `articles` (`DateIssued`, `UniqueName`, `HeaderText`, `HTML`, `LanguageID`) VALUES
('2020-03-11 13:50:10', 'orlovska-soutez-v-informatice', 'Orlovská Soutěž v Informatice', '<p>Ve středu 29.4.2020 pořádáme tradiční Orlovskou soutěž v informatice. Začátek je v 8.00 hodin.&nbsp;&nbsp;</p>', 0),
('2020-03-11 13:52:03', 'orlovska-soutez-v-informatice', 'Information Technologies Competition in Orlová', '<p>On Wednesday 29th of April we are going to host our classic competition of information technologies. The start is set to 8:00 in the morning.</p>', 1);
INSERT INTO `events` (`DateBegin`, `DateEnd`, `TitleText`, `ArticleUniqueName`, `Color`) VALUES
('2020-04-29 08:00:00', '2020-04-29 14:00:00', 'Orlovská Soutež v Informatice', 'orlovska-soutez-v-informatice', 65280);
INSERT INTO `pages` (`UniqueName`, `HeaderText`, `HTML`, `LanguageID`) VALUES
('dvpp', 'DVPP', '<p><strong>Nabídka DVPP</strong></p><p>Ministerstvo školství udělilo naší škole akreditaci k&nbsp;provádění těchto vzdělávacích programů zaměřených na další vzdělávání pedagogických pracovníků:&nbsp;</p><p><br></p><ul><li><strong>Profesionální postupy při zpracování textu - wordprocessing</strong></li><li>celkem 8 hodin, cena 950 Kč</li><li><strong>Využití PC pro výuku elektrotechnických a fyzikálních měření</strong></li><li>celkem 25 hodin, cena 2 500 Kč</li><li><strong>Technické kreslení a modelování v CAD systémech</strong></li><li>celkem 25 hodin, cena 2 500 Kč</li><li><strong>Tvorba databázových aplikací</strong></li><li>celkem 30 hodin, cena 3 000 Kč</li><li><strong>Tvorba webových aplikací</strong></li><li>celkem 30 hodin, cena 2 000 Kč</li><li><strong>Programování robotů Lego Mindstorms</strong></li><li>celkem 12 hodin, cena 1 200 Kč</li></ul><p><br></p><p>Vzdělávání budeme realizovat průběžně při minimálním počtu šesti účastníků.</p><p>Pro úhradu kurzovného lze použít <span style=\"color: rgb(255, 0, 0);\"><strong>prostředky poskytnuté školám v rámci šablon pro DVPP</strong></span>. Všechny programy jsou akreditovány pod č.j. MŠMT-5846/2017-1-467. Podrobné informace Vám poskytne Mgr. Pavel Michelsohn na telefonu 596 514 400.</p>', 0),
('dvpp', 'DVPP', '<p><strong>DVPP Offer</strong></p><p>The Ministry of Education has given us the right to perform the following educational programs for public educationalist:</p><p><br></p><ul><li><strong>Proffesional Word processing</strong></li><li>total 8 hours, price 950 Kč</li><li><strong>Usage of PC for Physics and Electrophysical&nbsp;</strong><b>measurements</b></li><li>total 25 hours, price 2 500 Kč</li><li><strong>Technical Drawing and Modeling using CAD</strong></li><li>total 25 hours, price 2 500 Kč</li><li><strong>Database Systems</strong></li><li>total 30 hours, price 3 000 Kč</li><li><b>Website Creation</b></li><li>total 30 hours, price 2 000 Kč</li><li><strong>Lego Mindstorms robot programming</strong></li><li>total 12 hours, price 1 200 Kč</li></ul><p><br></p><p>The education will be realised with minimum of six participants.</p><p>Payment can be done using <span style=\"color: rgb(255, 0, 0);\"><strong>the Czech Republic templates for schools in the DVPP program</strong></span>. Programs are acredited under the Czech Law MŠMT-5846/2017-1-467. You can find more information by calling the principal Pavel Michelsohn at +420 596 514 400.</p>', 1),
('o-skole', 'O Škole', '<p>\n</p><div>\n      <div>\n        <p>\n	<span style=\"font-size: 26px;\"><strong>Profilace školy</strong></span></p>\n<p>\n	Škola je orientována na výuku oboru Informační technologie. Tento obor \nje čtyřletý a&nbsp;poskytují střední vzdělání s&nbsp;maturitní zkouškou.</p>\n<p>\n	Naši absolventi nacházejí velmi dobré uplatnění na trhu práce \nv&nbsp;oblastech informačních a&nbsp;komunikačních technologií a&nbsp;v&nbsp;dalším \nvzdělávání na vysokých školách.</p>\n\n      </div>\n    </div>\n\n<p><br></p>', 0),
('z-historie-skoly', 'Z Historie Školy', '<p>\n</p><div>\n      <div>\n        <p>\n	Náš vzdělávací komplex vznikl v&nbsp;roce 1994&nbsp;jako NET OFFICE SCHOOL, spol.\n s&nbsp;r.o. pro pomaturitní studium oboru „Aplikace osobních počítačů\". \nNásledující rok byl vytvořen studijní obor Výpočetní technika \nv&nbsp;elektrotechnice, který byl od školního roku 1997/98 nahrazen novým \noborem Informační technologie.</p>\n<p>\n	Naše škola je soukromou vzdělávací institucí, která nabízí ucelený \nsystém vzdělávání od nultého ročníku střední školy, přes denní maturitní\n studium až po vzdělávací a rekvalifikační kurzy pro širokou veřejnost, \núřady práce a firmy i&nbsp;podniky v&nbsp;okolí.</p>\n<p>\n	<ins>Součásti školy dnes tvoří :</ins></p>\n<p>\n	Nultý ročník pro žáky 9.&nbsp;tříd ZŠ<br>\n	Denní maturitní studium oboru Informační technologie<br>\n	Zkrácené maturitní studium oboru Informační technologie<br>\n	Školení v&nbsp;rámci „Dalšího vzdělávání pedagogických pracovníků“<br>\n	Rekvalifikační kurzy pro širokou veřejnost<br>\n	&nbsp;</p>\n<p>\n	&nbsp;</p>\n<p>\n	Předkládáme Vám možnosti studia na naší škole. Své místo zde najdou \nvšichni žáci základních škol, kteří navštěvují v&nbsp;tomto školním roce \ndevátý ročník. Máme zájem o&nbsp;Vás všechny, kteří chcete ve své životě \ndosáhnout určitého stupně odborné kariéry. K&nbsp;tomu máme vybudovány \nvýukové technologie.</p>\n<p>\n	&nbsp;</p>\n<p>\n	<ins>Potřebujeme jediné :</ins><br>\n	Abyste chtěli a abyste si důvěřovali, že to , co chcete, také \ndosáhnete. Podmínky úspěšného studia na naší škole jsou tvrdé a nelze \nočekávat nic jiného, než neustálý tlak na Váš rozvoj. Naši úspěšní \nabsolventi nás však přesvědčují, že nastoupená cesta je správná. Studium\n na naší škole berte jako výzvu. Věřím, že pokud ji zvládnete, překonáte\n i&nbsp;každou další překážku ve svém životě.</p>\n<p>\n	Dovolte, abych závěrem konstatoval, že máme dobrý kolektiv, který umí \nzvládnout i&nbsp;krizové situace. Systematicky budujeme společenství \nmoderních řídících pracovníků. Rádi Vás mezi sebou přivítáme.</p>\n<p>\n	Mgr. Pavel Michelsohn, ředitel školy</p>\n\n      </div>\n    </div>\n\n<p><br></p><p><br></p>', 0),
('skolni-management', 'Školní Management', '<p><span style=\"font-size: 22px;\">Vedení Školy</span><br></p><table style=\"width: 100%;\"><tbody><tr><td class=\"\"><div>Titul<br></div></td><td class=\"\"><div>Jméno<br></div></td><td class=\"\"><div>E-Mail</div></td></tr><tr><td class=\"\"><div>Ředitel Školy<br></div></td><td class=\"\"><div>Mgr. Pavel Michelsohn<br></div></td><td class=\"\"><div>michelsohn@nosch.cz<br></div></td></tr><tr><td class=\"\"><div>Zástupce Ředitele<br></div></td><td class=\"\"><div>Ing. Joanna Pazdziorová<br></div></td><td class=\"\"><div>pazdziorova@post.cz<br></div></td></tr></tbody></table><p><br></p><p><span style=\"font-size: 24px;\">Předmětová Komise</span></p><table style=\"width: 100%;\"><tbody><tr><td class=\"\"><div>Komise<br></div></td><td class=\"\"><div>Vedoucí<br></div></td></tr><tr><td class=\"\"><div>Přírodovědných předmětů<br></div></td><td class=\"\"><div>Mgr. Alena Sovová<br></div></td></tr><tr><td class=\"\"><div>Odborných předmětů ICT<br></div></td><td class=\"\"><div>RNDr. Michaela Masná, Ph. D.<br></div></td></tr><tr><td class=\"\"><div>Humanitních předmětů<br></div></td><td class=\"\"><div>Mgr. Naďa Tománková<br></div></td></tr></tbody></table><p><br></p><p><span style=\"font-size: 24px;\">Školská Rada</span></p><table style=\"width: 100%;\"><tbody><tr><td class=\"\"><div>Složení<br></div></td><td class=\"\"><div>Zařazení<br></div></td></tr><tr><td class=\"\"><div>Šárka Hellerová<br></div></td><td class=\"\"><div>Předseda<br></div></td></tr><tr><td class=\"\"><div>Ing. Michaela Weirichová<br></div></td><td><div>Člen</div></td></tr><tr><td class=\"\"><div>Mgr. Marcela Adamovská<br></div></td><td><div>Člen<br></div></td></tr></tbody></table><p><br></p><p><span style=\"font-size: 24px;\">Poradní sbor školy</span></p><table style=\"width: 100%;\"><tbody><tr><td class=\"\"><div>Titul<br></div></td><td class=\"\"><div>Učitel<br></div></td></tr><tr><td class=\"\"><div>Výchovný Poradce<br></div></td><td class=\"\"><div>Mgr. Naďa Tománková<br></div></td></tr><tr><td><div>Školní metodik prevence<br></div></td><td class=\"\"><div>Mgr. Naďa Tománková<br></div></td></tr><tr><td class=\"\"><div>Koórdinátor ICT<br></div></td><td class=\"\"><div>Mgr. Pavel Michelsohn</div></td></tr><tr><td class=\"\"><div>Kordinátor ŠVP<br></div></td><td class=\"\"><div>Ing. Joanna Pazdziorová<br></div></td></tr><tr><td class=\"\"><div>Speciální Pedagog<br></div></td><td class=\"\"><div>Mgr. Marcela Adamovská<br></div></td></tr></tbody></table><p><br></p><p><br></p><p><br></p><p><br></p><p><br></p><p><br></p><p><br></p><p><br></p><p><br></p><p><br></p><p><br></p>', 0),
('skolska-rada', 'Školská Rada', '<p>\n</p><p>\n	Školská rada se podílí na&nbsp;správě školy. Má 3 členy, jednoho člena \njmenuje zřizovatel, jednoho člena volí zákonní zástupci nezletilých žáků\n a zletilí žáci a jednoho člena volí pedagogičtí pracovníci školy. \nFunkční období členů školské rady je tři roky. Školská rada zasedá \nnejméně dvakrát ročně a mezi její pravomoci patří:</p>\n<p>\n	- vyjadřuje se k&nbsp;návrhům školních vzdělávacích programů a k&nbsp;jeho uskutečňování,</p>\n<p>\n	- schvaluje výroční zprávu o&nbsp;činnosti školy,</p>\n<p>\n	- schvaluje školní řád, stipendijní řád, a navrhuje jejich změny,</p>\n<p>\n	- schvaluje pravidla pro&nbsp;hodnocení výsledků vzdělávání žáků v základních a středních školách,</p>\n<p>\n	- podílí se na&nbsp;zpracování koncepčních záměrů rozvoje školy,</p>\n<p>\n	- projednává inspekční zprávy České školní inspekce,</p>\n<p>\n	- podává podněty a oznámení řediteli školy, zřizovateli, orgánům \nvykonávajícím státní správu ve&nbsp;školství a dalším orgánům státní správy,</p>\n<p>\n	- podává návrh na vyhlášení konkursu na ředitele</p>\n\n<p><br></p><p><br></p>', 0),
('vychovne-poradenstvi', 'Výchovné Poradenství', '<p>\n</p><div>\n      <div><span style=\"font-size: 24px;\"><strong>Výchovné poradenství</strong></span>\n<p>\n	Výchovný poradce <strong>Mgr. Naďa Tománková:</strong></p>\n<p>\n	- spolupracuje a komunikuje s&nbsp;rodiči v&nbsp;rámci třídních a konzultačních schůzek a individuálních pohovorů</p>\n<p>\n	- spolupracuje s&nbsp;třídními učiteli i ostatními pedagogickými pracovníky \npři individuálním přístupu ke&nbsp;studentům problémovým, zdravotně nebo \nsociálně handicapovaným a v&nbsp; neposlední řadě i nadaným</p>\n<p>\n	- provádí individuální pohovory se&nbsp; studenty i rodiči problémových a zdravotně postižených žáků</p>\n<p>\n	- zve studenty a jejich rodiče k projednávání špatných studijních nebo kázeňských výsledků</p>\n<p>\n	- spolupracuje s&nbsp;odborníky s&nbsp;pedagogicko psychologické poradny</p>\n<p>\n	- dohlíží nad&nbsp;zohledněním studentů s&nbsp;těmito specifickými vývojovými poruchami učení</p>\n<p>\n	- předává informace z&nbsp;oblasti výchovného poradenství všem pedagogickým pracovníkům</p><p><br></p><span style=\"font-size: 24px;\"><strong>Školní metodik prevence\n</strong></span><p>\n	Školní metodik prevence <strong>Mgr. Naďa Tománková:</strong></p>\n<p>\n	- připravuje školní preventivní strategií na daný školní rok</p>\n<p>\n	- podílí se na organizací adaptačních kurzů pro žáky 1. ročníků</p>\n<p>\n	- zaměřuje se na prevenci v oblasti závislostí, šikany, záškoláctví, kriminality apod.</p>\n<p>\n	- shromažďuje aktuální informační materiály k problematice prevence</p>\n<p>\n	- navrhuje a zajišťuje besedy a aktivity školy</p>\n<p>\n	- spolupracuje s Pedagogicko psychologickou poradnou v Orlové<span style=\"font-size: 24px;\"><br></span></p><p><span style=\"font-size: 24px;\"><br></span></p><p><span style=\"font-size: 24px;\"></span><span style=\"font-size: 24px;\"><strong>Konzultační hodiny pro žáky a rodiče:</strong></span> <br></p><p>- středa: &nbsp;10.15 - 11.00 hod.\n	\n		<br></p><p>- čtvrtek: 09.25 - 10.15 hod.\n	\n		<br></p><p>- mimo určený čas po ústní dohodě\n	\n		<strong></strong><br><strong>Telefon:</strong>\n	\n		- 596 514 400, 603 219 193\n</p><span style=\"font-size: 24px;\">​</span>\n\n      </div>\n    </div>\n\n<p><br></p><p><br></p><p><br></p>', 0),
('nabidka-studia', 'Nabídka Studia', '<p>\n</p><div>\n      <div>\n        <p>\n	<strong>Nabídka studia - Informační technologie(4letý maturitní obor)</strong><br>\n	</p>\n<p>\n	Určeno pro absolventy základních škol.</p>\n<p>\n	Studijní plán oboru odpovídá požadavkům na profil absolventa střední odborné&nbsp;školy:</p>\n<p>\n	<strong>Odborné vzdělání oboru zahrnuje:</strong></p>\n<p>\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; všestrannou znalost práce s&nbsp;uživatelskými programy</p>\n<p>\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; počítačovou grafiku, kreslení v CAD systémech</p>\n<p>\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; přehled technického vybavení počítačů</p>\n<p>\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; stavbu a pokročilou správu počítačových&nbsp;sítí</p>\n<p>\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; virtualizaci serverů a desktopů</p>\n<p>\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; databázové aplikace</p>\n<p>\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; elektrotechnická měření na PC</p>\n<p>\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tvorbu informačních systémů, široké využití internetu</p>\n<p>\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pokročilou tvorbu a správu webových stránek</p>\n<p>\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; programování v různých jazycích</p>\n<p>\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; programování robotů LEGO</p>\n<p>\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; po celou dobu studia je vyučován anglický jazyk a odborná technická angličtina</p>\n<p>\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; studenti získají i&nbsp;ekonomické znalosti (ekonomika, management)</p>\n<div>\n	<hr>\n	<p>\n		&nbsp;</p>\n	<p>\n		<strong>Přijímací řízení ve školním roce 2020/2021</strong></p>\n	<p>\n		&nbsp;</p>\n	<p>\n		<strong>Do 1. kola přijímacího řízení&nbsp;přijímáme přihlášky ke studiu SOŠ do 1. března 2020 do maturitního oboru</strong><strong>&nbsp;</strong><strong>18-20-M/01 Informační technologie</strong><strong>.</strong><strong>&nbsp;&nbsp;</strong><strong>Přijato bude celkem 30 uchazečů.</strong><strong>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong><em><strong>&nbsp; &nbsp; &nbsp;</strong></em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>\n</div>\n<p>\n	<strong>Kritéria přijímacího řízení pro školní rok 2020/2021 <a href=\"http://www.nosch.cz/other_files/pzkousky/pz.pdf\">zde</a>.</strong></p>\n<p>\n	<em>PŘIHLÁŠKA KE STUDIU <ins><a href=\"http://www.nosch.cz/other_files/pzkousky/277.pdf\" target=\"_blank\">zde</a></ins></em></p>\n<p>\n	Požadavky pro jednotnou přijímací zkoušku z matematiky<em> <ins><a href=\"http://www.nosch.cz/other_files/pzkousky/279.pdf\" target=\"_blank\">zde</a></ins></em></p>\n<p>\n	Požadavky pro jednotnou přijímací zkoušku z českého jazyka a literatury <ins><em><a href=\"http://www.nosch.cz/other_files/pzkousky/280.pdf\" target=\"_blank\">zde</a></em></ins></p>\n<p>\n	Přijímací řízení se koná na základě zákona č. 561/2004 Sb. (školský \nzákon) ve znění pozdějších předpisů a řídí se vyhláškou č. 353/2016 Sb. o\n přijímacím řízení ke střednímu vzdělávání.</p>\n<p>\n	Informace o podmínkách a způsobu přijímání žáků ke studiu v 1. kole a \ndalších kolech přijímacího řízení pro školní rok 2020/2021:</p>\n<p>\n	<strong>Přihlášky ke studiu</strong></p>\n<p>\n	· &nbsp; &nbsp;Svůj zájem o studium na škole vyjádří uchazeči podáním přihlášky ke studiu.</p>\n<p>\n	· &nbsp; &nbsp;Barevné rozlišení formulářů přihlášek není podmínkou.</p>\n<p>\n	· &nbsp; &nbsp;Uchazeči podávají přihlášky přímo řediteli střední školy - pro první kolo do <strong>1. března 2020</strong>.</p>\n<p>\n	· &nbsp; &nbsp;Pro první kolo může uchazeč podat nejvýše dvě přihlášky. Pokud \nuchazeč podává dvě přihlášky, uvede na každé přihlášce také&nbsp;údaj o škole\n a oboru vzdělání, kam podává druhou přihlášku. <strong>Pořadí škol se uvádí na obou přihláškách stejné! </strong>Uvedené\n pořadí škol na přihlášce ke studiu není pro uchazeče závazné, o volbě \nškoly a oboru vzdělání rozhodne uplatněním svého zápisového lístku.</p>\n<p>\n	· &nbsp; &nbsp; &nbsp;Každé přihlášce je přidělen kód – registrační číslo a tento je oznámen zákonnému zástupci uchazeče.</p>\n<p>\n	· &nbsp; &nbsp; &nbsp;Lékařský posudek o zdravotní způsobilosti uchazeče se na přihlášce nevyžaduje.</p>\n<p>\n	· &nbsp; &nbsp; &nbsp;Uchazeč se speciálními vzdělávacími potřebami přikládá k&nbsp;přihlášce „Doporučení školského poradenského zařízení“.</p>\n<p>\n	<strong>Přijímací zkouška</strong></p>\n<p>\n	· &nbsp; &nbsp; &nbsp;V&nbsp;přijímacím řízení se <strong>pouze </strong>přijímací zkouška formou jednotné zkoušky.</p>\n<p>\n	· &nbsp; Jednotná zkouška se skládá z&nbsp;písemného testu z&nbsp;<strong>českého jazyka a literatury</strong> a písemného testu z <strong>matematiky a její aplikace</strong>.</p>\n<p>\n	· &nbsp; &nbsp; &nbsp;Jednotná zkouška se koná ve dvou termínech:</p>\n<p>\n	· &nbsp; &nbsp; Každý uchazeč může písemné testy konat dvakrát - v&nbsp;prvním \nstanoveném termínu ve škole uvedené na přihlášce v&nbsp;prvním pořadí, ve \ndruhém stanoveném termínu ve škole uvedené na přihlášce ve druhém \npořadí.</p>\n<p>\n	· &nbsp; &nbsp; &nbsp;Uchazeči se do celkového hodnocení započítává lepší výsledek.</p>\n<p>\n	· &nbsp; &nbsp; Ředitel střední školy rozhodne o uzpůsobení podmínek přijímací \nzkoušky uchazečům s doloženými speciálními vzdělávacími potřebami.</p>\n<p>\n	<strong>Zveřejnění kritérií přijímacího řízení</strong></p>\n<p>\n	· &nbsp; &nbsp; Kritéria přijímacího řízení je povinen ředitel školy stanovit a \nzveřejnit pro 1. kolo přijímacího řízení do 31. ledna (podle § 60 odst.4\n písm. a) školského zákona).</p>\n<p>\n	· &nbsp; &nbsp; Pro další kola příjímacího řízení zveřejňuje ředitel školy \nkritéria nejpozději k&nbsp;datu vyhlášení příslušného kola příjímacího \nřízení.</p>\n<p>\n	<strong>Vyhlášení výsledků přijímacího řízení</strong></p>\n<p>\n	· &nbsp; &nbsp;Podle výsledků dosažených jednotlivými uchazeči při přijímacím \nřízení stanoví ředitel školy jejich pořadí a zveřejní seznam přijatých \nuchazečů a nepřijatým uchazečům nebo zákonným zástupcům nezletilých \nnepřijatých uchazečů odešle rozhodnutí o nepřijetí.</p>\n<p>\n	· &nbsp; &nbsp; Rozhodnutí, kterými se vyhovuje žádosti o přijetí ke vzdělávání, \nse oznamují zveřejněním seznamu uchazečů pod přiděleným registračním \nčíslem s&nbsp;výsledkem řízení u každého uchazeče.</p>\n<p>\n	· &nbsp; &nbsp; Seznam se zveřejňuje na veřejně přístupném místě ve škole a též \nzpůsobem umožňujícím dálkový přístup, a to alespoň na dobu 15 dnů. \nZveřejněním seznamu se považují rozhodnutí, kterými se vyhovuje žádostem\n o přijetí ke vzdělávání, za oznámená.</p>\n<p>\n	<strong>Zápisový lístek</strong></p>\n<p>\n	· &nbsp; &nbsp; &nbsp;Zápisový lístek vydává žákům základní školy tato základní škola, ostatním uchazečům příslušný krajský úřad.</p>\n<p>\n	· &nbsp; &nbsp;Svůj úmysl vzdělávat se v&nbsp;dané střední škole potvrdí uchazeč nebo \nzákonný zástupce nezletilého uchazeče odevzdáním zápisového lístku \nřediteli školy, který rozhodl o jeho přijetí ke vzdělávání, a to \nnejpozději do 10 pracovních dnů ode dne oznámení rozhodnutí. Zápisový \nlístek se také považuje za včas odevzdaný, pokud byl v&nbsp;této lhůtě předán\n k&nbsp;přepravě provozovateli poštovních služeb.</p>\n<p>\n	· &nbsp; &nbsp; Zápisový lístek může uchazeč uplatnit jen jednou. (To neplatí \npouze v&nbsp;případě, že uchazeč chce uplatnit zápisový lístek na škole,&nbsp;kde \nbyl přijat na základě odvolání).</p>\n<p>\n	<strong>Odvolání proti nepřijetí</strong></p>\n<p>\n	· &nbsp; &nbsp; Podle § 60 odst. 19 školského zákona v&nbsp;platném znění je proti \nrozhodnutí ředitele školy přípustné odvolání do tří pracovních dnů od \njeho doručení. Odvolání se podává u ředitele střední školy. O odvolání \nrozhodne Krajský úřad Moravskoslezského kraje, odbor školství, mládeže a\n sportu.</p>\n<p>\n	<strong>Další kola přijímacího řízení</strong></p>\n<p>\n	· &nbsp; &nbsp; Školský zákon přímo stanoví, že ředitel školy může po ukončení 1.\n kola přijímacího řízení vyhlásit další kola přijímacího řízení \nk&nbsp;naplnění předpokládaného stavu žáků.</p>\n<p>\n	· &nbsp; &nbsp; Pro další kola přijímacího řízení zveřejňuje ředitel školy \nkritéria nejpozději k&nbsp;datu vyhlášení příslušného kola přijímacího \nřízení.</p>\n<hr>\n<p>\n	<strong>Pro školní rok 2020/2021 přijímáme přihlášky ke studiu pro:</strong></p>\n<p>\n	<strong></strong></p>\n<p>\n	&nbsp;</p>\n<p>\n	<strong>4letý obor 18-20-M/01 Informační technologie - přijato bude celkem 30 uchazečů</strong></p>\n<p>\n	&nbsp;</p>\n<p>\n	Bližší informace budeme postupně zveřejňovat na webových stránkách školy, na Dnech otevřených dveří.</p>\n<p>\n	<strong>DALŠÍ INFORMACE ke studiu Vám poskytne:</strong></p>\n<p>\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Mgr. Naďa Tománková, výchovný poradce</p>\n<p>\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Mgr. Pavel Michelsohn, ředitel školy, i&nbsp;ostatní vyučující.</p>\n\n      </div>\n    </div>\n\n<p><br></p><p><br></p>', 0);
('nulty-rocnik', 'Nultý Ročník', '<p>\n</p><p>\n	Oznamujeme všem zájemcům , že v&nbsp;i&nbsp;v&nbsp;tomto školním roce budeme otevírat \nnultý ročník. Konat se bude od 6. února do 2. dubna 2020.<br>\n	Zájemci se mohou hlásit mailem, telefonicky nebo osobně na sekretariátě školy.<br>\n	<strong>Termín zahájení je 6.&nbsp;2.&nbsp;2020</strong> ve 14.30&nbsp;hodin v&nbsp;budově školy.<br>\n	Výuka bude dále probíhat&nbsp;<strong>každý čtvrtek od 14.30&nbsp;hodin</strong>&nbsp;a\n to v&nbsp;délce tří vyučovacích hodin (s&nbsp;výjímkou jarních a velikonočních \nprázdnin). Náplň bude tvořit učivo, které Vás připraví k přijímacím \nzkouškám na střední školu (matematika, český jazyk a informatika).<br>\n	Přesný rozvrh jednotlivých předmětů Vám bude sdělen na první vyučovací hodině.</p>\n<p>\n	<strong>Další informace</strong></p>\n<p>\n	Nultý ročník je určen žákům 9.&nbsp;tříd jako příprava k&nbsp;přijímacímu řízení \nke studiu na střední škole. Žák studuje jednou týdně (vždy ve čtvrtek \nodpoledne) český jazyk, matematiku, informatiku a prohlubuje si studijní\n předpoklady. Cílem je pomoci žákovi, aby se seznámil se středoškolským \npřístupem ve výuce a také svojí budoucí disciplínou. Zároveň nám jde \no&nbsp;ujasnění a rozšíření znalostí z&nbsp;matematiky, jazyka českého a \ninformatiky. Žák na naši školu nemusí nastoupit.</p>\n<p>\n	<strong>V&nbsp;čem se naše střední škola liší od jiných středních škol?</strong><br>\n	Žáci středních škol musí počítat s&nbsp;tím, že budou konat jednotnou \nmaturitní zkoušku. Jestliže potom bude stát před maturitní komisí žák \nSOU nebo integrované střední školy, bude se muset měřit s&nbsp;našimi \nabsolventy. Vzhledem k&nbsp;náročnosti studia na naší škole jsme přesvědčeni,\n že naši absolventi budou mít daleko lepší pozici uplatnit se v&nbsp;praxi \nnebo pokračovat v&nbsp;dalším studiu. Naši žáci mají vytvořeny dobré podmínky\n pro studium: nejen kvalitní vzdělávací systém, ale také velmi nízký \npočet studentů ve skupinách. Vstoupíte do dobrého kolektivu studentů a \nučitelů s&nbsp;velmi úzkými mezilidskými vztahy. Ve výuce je posílena výuka \nvýpočetní techniky. Žáci mají přístup na Internet, mohou používat \nkopírovací techniku a DTP pracoviště školy. Žákům pomáháme budovat \nsebevědomí. Žáci absolvují souvislou odbornou praxi, která je připraví \npro vstup do podnikové sféry. Žák má k&nbsp;dispozici školní knihovnu a \nučebny, kde se připravuje na výuku a řeší seminární práce. Škola má za \ncíl vvchovávat technicky zaměřené odborníky, preferuje logické myšlení, \nschopnosti samostatně analyzovat technické problémy a hledat jejich \nkonkrétní řešení.</p>\n<p>\n	<strong>Školné</strong><br>\n	Poplatek za nultý ročník činí 500&nbsp;Kč.</p>\n\n<p><br></p><p><br></p>', 0),
('videa-ze-skoly', 'Videa ze Školy', '<p>\n</p><div>\n      <div>\n        <p>\n	Zde můžete vidět videa ze školy, která byla vybrána z prací studentů I. ročníku:</p>\n<p>\n	<a href=\"https://youtu.be/G1ZCOrRVLww\" target=\"_blank\">Daniel Dobrý</a><br>\n	<a href=\"https://youtu.be/HxudZ3SaMPw\" target=\"_blank\">David Dičík</a><br>\n	<a href=\"https://youtu.be/M3uYtLBl40o\" target=\"_blank\">Aneta Konkolská</a><br>\n	<a href=\"https://youtu.be/f82XGgoFSlg\" target=\"_blank\">Matěj Vlach</a><br>\n	<a href=\"https://youtu.be/chNVrw9K92M\" target=\"_blank\">Adam Pustowka</a></p>\n<p>\n	<a href=\"https://www.facebook.com/SosNetOfficeOrlovaSpolSRO/?ref=aymt_homepage_panel\" target=\"_blank\">Daniel Dobrý - video ze školy</a></p>\n\n      </div>\n    </div>\n\n<p><br></p><p><br></p>', 0);
